import PropTypes from 'prop-types';
import React from 'react';
import PageHeading from '@components/admin/cms/PageHeading';
import Layout from "@evershop/evershop/src/modules/cms/pages/frontStore/all/Layout";
import {useTranslation} from "react-i18next";
import '../../../../i18n'

export default function CategoryEditPageHeading({ backUrl, category }) {
  const {t, i18n} = useTranslation()
  return (
    <PageHeading
      backUrl={backUrl}
      heading={category ? `Editing ${category.name}` : t('createcategory')}
    />
  );
}

CategoryEditPageHeading.propTypes = {
  backUrl: PropTypes.string.isRequired,
  category: PropTypes.shape({
    name: PropTypes.string
  })
};

CategoryEditPageHeading.defaultProps = {
  category: {}
};

export const layout = {
  areaId: 'content',
  sortOrder: 5
};

export const query = `
  query Query {
    category(id: getContextValue("categoryId", null)) {
      name
    }
    backUrl: url(routeId: "categoryGrid")
  }
`;
