import PropTypes from 'prop-types';
import React from 'react';
import Button from '@components/common/form/Button';
import Layout from "@evershop/evershop/src/modules/cms/pages/frontStore/all/Layout";
import {useTranslation} from "react-i18next";
import '../../../../i18n'

export default function NewCategoryButton({ newCateoryUrl }) {
  const {t, i18n} = useTranslation()
  return <Button url={newCateoryUrl} title= {t('newcategory')} />;
}

NewCategoryButton.propTypes = {
  newCateoryUrl: PropTypes.string.isRequired
};

export const layout = {
  areaId: 'pageHeadingRight',
  sortOrder: 10
};

export const query = `
  query Query {
    newCateoryUrl: url(routeId: "categoryNew")
  }
`;
