import PropTypes from 'prop-types';
import React from 'react';
import PageHeading from '@components/admin/cms/PageHeading';
import Layout from "@evershop/evershop/src/modules/cms/pages/frontStore/all/Layout";
import {useTranslation} from "react-i18next";
import '../../../../i18n'

export default function ProductEditPageHeading({ backUrl, product }) {
  const {t, i18n} = useTranslation()
  return (
    <PageHeading
      backUrl={backUrl}
      heading={product ? `Editing ${product.name}` : t('createnewproduct')}
    />
  );
}

ProductEditPageHeading.propTypes = {
  backUrl: PropTypes.string.isRequired,
  product: PropTypes.shape({
    name: PropTypes.string.isRequired
  })
};

ProductEditPageHeading.defaultProps = {
  product: null
};

export const layout = {
  areaId: 'content',
  sortOrder: 5
};

export const query = `
  query Query {
    product(id: getContextValue("productId", null)) {
      name
    }
    backUrl: url(routeId: "productGrid")
  }
`;
