import React, {useState} from 'react';
import Area from '@components/common/Area';
import './Layout.scss';
import './tailwind.scss';
import { useTranslation } from 'react-i18next';
import '../../../../i18n'


export default function AdminLayout() {
  const { t, i18n } = useTranslation();

  const [isTurkish, setIsTurkish] = useState(true);

  const toggleLanguage = async () => {
    const newLang = isTurkish ? 'en' : 'tr';
    await i18n.changeLanguage(newLang);
    setIsTurkish(!isTurkish);
  };
  return (

      <>

        <div className="header">
          <Area id="header" noOuter />
        </div>
        <div className="content-wrapper">
          <div className="admin-navigation">
            <Area id="adminNavigation" noOuter />
          </div>
          <div className="main-content">
            <Area id="content" className="main-content-inner" />
            <div className="footer">
              <div className="copyright">
                <span>Copyright © 2021 EverShop</span>
              </div>
              <div className="version">
                <span>Version 1.0 dev</span>
              </div>
              <div>
                <button onClick={toggleLanguage}>{isTurkish ? 'English' : 'Türkçe'}</button>
              </div>
            </div>
          </div>

        </div>

      </>
  );
}

export const layout = {
  areaId: 'body',
  sortOrder: 10
};
