import PropTypes from 'prop-types';
import React from 'react';
import Button from '@components/common/form/Button';
import Layout from "@evershop/evershop/src/modules/cms/pages/frontStore/all/Layout";
import {useTranslation} from "react-i18next";
import '../../../../i18n'

export default function NewPageButton({ newPageUrl }) {
  const {t, i18n} = useTranslation()
  return <Button url={newPageUrl} title={t('newpage')} />;
}

NewPageButton.propTypes = {
  newPageUrl: PropTypes.string.isRequired
};

export const layout = {
  areaId: 'pageHeadingRight',
  sortOrder: 10
};

export const query = `
  query Query {
    newPageUrl: url(routeId: "cmsPageNew")
  }
`;
