import PropTypes from 'prop-types';
import React from 'react';
import { Card } from '@components/admin/cms/Card';
import Layout from "@evershop/evershop/src/modules/cms/pages/frontStore/all/Layout";
import {useTranslation} from "react-i18next";
import '../../../../i18n'

export default function ShippingSettingMenu({ shippingSettingUrl }) {
  const {t, i18n} = useTranslation()
  return (
    <Card.Session title={<a href={shippingSettingUrl}>{t('shipping')}</a>}>
      <div>{t('shippingtext')}</div>
    </Card.Session>
  );
}

ShippingSettingMenu.propTypes = {
  shippingSettingUrl: PropTypes.string.isRequired
};

export const layout = {
  areaId: 'settingPageMenu',
  sortOrder: 15
};

export const query = `
  query Query {
    shippingSettingUrl: url(routeId: "shippingSetting")
  }
`;
