import PropTypes from 'prop-types';
import React from 'react';
import { useAppState } from '@components/common/context/app';
import { Card } from '@components/admin/cms/Card';
import Layout from "@evershop/evershop/src/modules/cms/pages/frontStore/all/Layout";
import {useTranslation} from "react-i18next";
import '../../../../i18n'

export default function BestCustomers({ listUrl }) {
  const {t, i18n} = useTranslation()
  const context = useAppState();
  const currency = context.currency || 'USD';
  const customers = context.bestCustomers || [];

  return (
    <Card
      title={t('bestcustomer')}
      actions={[
        {
          name: t('allcustomers'),
          onAction: () => {
            window.location.href = listUrl;
          }
        }
      ]}
    >
      <Card.Session>
        <table className="listing">
          <thead>
            <tr>
              <th>{t('fullName')}</th>
              <th>{t('orders')}</th>
              <th>{t('total')}</th>
            </tr>
          </thead>
          <tbody>
            {customers.map((c, i) => {
              const grandTotal = new Intl.NumberFormat('en', {
                style: 'currency',
                currency
              }).format(c.total);
              return (
                // eslint-disable-next-line react/no-array-index-key
                <tr key={i}>
                  <td>
                    <a href={c.editUrl || ''}>{c.full_name}</a>
                  </td>
                  <td>{c.orders}</td>
                  <td>{grandTotal}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </Card.Session>
    </Card>
  );
}

BestCustomers.propTypes = {
  listUrl: PropTypes.string.isRequired
};
