import PropTypes from 'prop-types';
import React from 'react';
import { Card } from '@components/admin/cms/Card';
import Layout from "@evershop/evershop/src/modules/cms/pages/frontStore/all/Layout";
import {useTranslation} from "react-i18next";
import '../../../../i18n'

export default function PaymentSettingMenu({ paymentSettingUrl }) {
  const {t, i18n} = useTranslation()
  return (
      <Card.Session title={<a href={paymentSettingUrl}>{t('paymentsetting')}</a>}>
        <div>{t('configureavaible')}</div>
      </Card.Session>
  );
}

PaymentSettingMenu.propTypes = {
  paymentSettingUrl: PropTypes.string.isRequired
};

export const layout = {
  areaId: 'settingPageMenu',
  sortOrder: 10
};

export const query = `
  query Query {
    paymentSettingUrl: url(routeId: "paymentSetting")
  }
`;
