import PropTypes from 'prop-types';
import React from 'react';
import { Card } from '@components/admin/cms/Card';
import Layout from "@evershop/evershop/src/modules/cms/pages/frontStore/all/Layout";
import {useTranslation} from "react-i18next";
import '../../../../i18n'

export default function StoreSettingMenu({ storeSettingUrl }) {
  const {t, i18n} = useTranslation()
  return (
      <Card.Session title={<a href={storeSettingUrl}>{t('storesetting')}</a>}>
        <div>{t('storeinformation')}</div>
      </Card.Session>
  );
}

StoreSettingMenu.propTypes = {
  storeSettingUrl: PropTypes.string.isRequired
};

export const layout = {
  areaId: 'settingPageMenu',
  sortOrder: 5
};

export const query = `
  query Query {
    storeSettingUrl: url(routeId: "storeSetting")
  }
`;
