import PropTypes from 'prop-types';
import React from 'react';
import { Card } from '@components/admin/cms/Card';
import Layout from "@evershop/evershop/src/modules/cms/pages/frontStore/all/Layout";
import {useTranslation} from "react-i18next";
import '../../../../i18n'

export default function TaxSettingMenu({ taxSettingUrl }) {
  const {t, i18n} = useTranslation()
  return (
      <Card.Session title={<a href={taxSettingUrl}>{t('taxsetting')}</a>}>
        <div>{t('taxrates')}</div>
      </Card.Session>
  );
}

TaxSettingMenu.propTypes = {
  taxSettingUrl: PropTypes.string.isRequired
};

export const layout = {
  areaId: 'settingPageMenu',
  sortOrder: 20
};

export const query = `
  query Query {
    taxSettingUrl: url(routeId: "taxSetting")
  }
`;
